import React from 'react'
import styled from 'styled-components'


function Pin(props) {

  let { urls, alt_description } = props;

  return (
    <Wrapper>
      <Container>
        <div className='cards'>
          <img src={urls?.regular} alt="pin " />
          {/* <img src="https://images.unsplash.com/photo-1479839672679-a46483c0e7c8?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1310&q=80" alt=" " /> */}
          <p className='descripton' >{alt_description} </p>
        </div>

      </Container>

    </Wrapper>
  )
}

export default Pin

const Wrapper = styled.div`
  display: inline-flex;
`;
const Container = styled.div`
display: flex;
align-items: center;
justify-content: center;
box-sizing: border-box;
cursor: pointer;
width: 236px;
padding: 2px;

.descripton {
  display: none
}

.cards {
  width: 100%;
}
img {    
    width: 100%;
    cursor: zoom-in;
    border: 2px solid white;
    border-radius: 16px;
    object-fit: cover;


    &:hover {      
        border: 2px solid red;
    }}

   .cards:hover .descripton {
    display: block;
    position: absolute;
    
   }
`
